!important;			font-weight: <?php echo $divi_powerful_last_menu_item_button_font_weight; ?>!important;			text-transform: <?php if($divi_powerful_last_menu_item_button_uppercase) { ?>uppercase<?php } else { ?>none<?php } ?>!important;			background-color: <?php echo $divi_powerful_last_menu_item_button_background_color; ?>!important;			color: <?php echo $divi_powerful_last_menu_item_button_text_color; ?>!important;			transition: all 0.35s ease;		}		.free-last-menu-item-button #main-header #top-menu>li:last-child>a,			#main-header #top-menu>li.free-menu-button>a {			margin-top: -<?php echo $divi_powerful_last_menu_item_button_padding_top_bottom + 2; ?>px!important;		}		.free-last-menu-item-button #main-header #top-menu>li:last-child>a:hover, .free-last-menu-item-button #main-header #mobile_menu>li:last-child>a:hover, .free-last-menu-item-button .et_slide_in_menu_container #mobile_menu_slide>li:last-child>a:hover,			#main-header #top-menu>li.free-menu-button>a:hover, #main-header #mobile_menu>li.free-menu-button>a:hover, .et_slide_in_menu_container #mobile_menu_slide>li.free-menu-button>a:hover,				.free-last-menu-item-button.free-body-summary-cart-search-menu .free-header-menu .et_mobile_menu>li:nth-last-child(4)>a:hover,					.free-last-menu-item-button.free-body-summary-search-menu .free-header-menu .et_mobile_menu>li:nth-last-child(3)>a:hover,						.free-last-menu-item-button.free-body-summary-cart-menu .free-header-menu .et_mobile_menu>li:nth-last-child(2)>a:hover,							.free-last-menu-item-button.free-body-summary-none-menu .free-header-menu .et_mobile_menu>li:last-child>a:hover,								.free-header-menu .et_mobile_menu>li.free-menu-button>a:hover,									.free-last-menu-item-button.free-body-summary-cart-search-menu .free-header-menu nav>ul>li:nth-last-child(4)>a:hover,										.free-last-menu-item-button.free-body-summary-search-menu .free-header-menu nav>ul>li:nth-last-child(3)>a:hover,											.free-last-menu-item-button.free-body-summary-cart-menu .free-header-menu nav>ul>li:nth-last-child(2)>a:hover,												.free-last-menu-item-button.free-body-summary-none-menu .free-header-menu nav>ul>li:last-child>a:hover,													.free-header-menu nav>ul>li.free-menu-button>a:hover {			background-color: <?php echo $divi_powerful_last_menu_item_button_background_color_hover; ?>!important;			color: <?php echo $divi_powerful_last_menu_item_button_text_color_hover; ?>!important;			border-color: <?php echo $divi_powerful_last_menu_item_button_border_color_hover; ?>!important;		}				<?php		// Mobile menu fixed container height		if($divi_powerful_mobile_menu_fixed) { ?>			.free-mobile-menu-fixed #main-header #mobile_menu {				max-height: <?php echo $divi_powerful_mobile_menu_fixed_height; ?>vh!important;			}		<?php		} ?>	</style> <?php}// ------------------ CSS Edit End ----------------------// ------------------ Add Search & Cart Start ----------------------function divi_powerful_menu_add_search_cart( $items, $args ) {	$divi_powerful_header_show_cart = get_option( 'divi_powerful_header_show_cart' );	$divi_powerful_header_show_search = get_option( 'divi_powerful_header_show_search' );	if (strpos($args->menu_class, 'fullwidth-menu') !== false) {		if ($divi_powerful_header_show_cart) {			if ( class_exists( 'woocommerce' ) ) {				$items .= '<li class="free-cart-menu">' . dp_show_cart_total() . '</li>';			}		}		if ($divi_powerful_header_show_search) {			$dp_search_outer = 	sprintf( '<input type="search" class="et-search-field" placeholder="%1$s" value="%2$s" name="s" title="%3$s" />',									esc_attr__( 'Search &hellip;', 'Divi' ),									get_search_query(),									esc_attr__( 'Search for:', 'Divi' )								);			$dp_search_action = esc_url( home_url( '/' ) );			$items .= 	'<li class="free-search-menu">							<a class="free-search-icon-link" href="#">								<span class="free-search-icon"></span>							</a>						</li>						<li class="free-search-container">							<div class="free-search-outer">								<form role="search" method="get" class="et-search-form" action="' . $dp_search_action . '">' .	$dp_search_outer . '								</form>								<span class="free-search-close"></span>							</div>						</li>';		}	}	return $items;}// ------------------ Add Search & Cart End ----------------------// ------------------ Show Cart Function Start ---------------------if ( ! function_exists( 'dp_show_cart_total' ) ) {	function dp_show_cart_total( $args = array() ) {		if ( ! class_exists( 'woocommerce' ) || ! WC()->cart ) {			return;		}		$defaults = array(			'no_text' => false,		);		$args = wp_parse_args( $args, $defaults );		$items_number = WC()->cart->get_cart_contents_count();				$url = function_exists( 'wc_get_cart_url' ) ? wc_get_cart_url() : WC()->cart->get_cart_url();		$dp_cart = sprintf (			'<a href="%1$s" class="et-cart-info">				<span><sup class="free-cart-total">%2$s</sup></span>			</a>',			esc_url( $url ),			( ! $args['no_text']				? esc_html( sprintf(					_nx( '%1$s', '%1$s', $items_number, 'WooCommerce items number', 'Divi' ),					number_format_i18n( $items_number )				) )				: ''			)		);		return $dp_cart;	}}// ------------------ Show Cart Function End ---------------------// -------------- Archive Pre-Title Shortcode Start ----------------function divi_powerful_archive_category_page_header_pre_title() {	$divi_powerful_archive_category_page_header_tag_text = get_option( 'divi_powerful_archive_category_page_header_tag_text', 'Posts Tagged - ' );	$divi_powerful_archive_category_page_header_category_text = get_option( 'divi_powerful_archive_category_page_header_category_text', 'Latest Blogs - ' );	$divi_powerful_archive_category_page_header_author_text = get_option( 'divi_powerful_archive_category_page_header_author_text', 'Articles Posted by ' );	$divi_powerful_archive_category_page_header_archive_text = get_option( 'divi_powerful_archive_category_page_header_archive_text', 'Archive - ' );	$divi_powerful_archive_category_page_header_search_text = get_option( 'divi_powerful_archive_category_page_header_search_text', 'Search Results for: ' );	$divi_powerful_archive_category_page_header_cpt_separator = get_option( 'divi_powerful_archive_category_page_header_cpt_separator', ' - ' );	$divi_powerful_archive_category_page_header_pre_title = '';		// $classes = get_body_class();	// if (in_array('et-pb-preview',$classes)) {		if ( is_et_pb_preview() ) {		$divi_powerful_archive_category_page_header_pre_title = '[Pre-Title]';	} else {			if  ( is_tag() || is_category() || is_author() || is_archive() || is_search() ) {			if  ( is_tag() ) {				$divi_powerful_archive_category_page_header_pre_title = $divi_powerful_archive_category_page_header_tag_text;			} elseif  ( is_category() ) {				$divi_powerful_archive_category_page_header_pre_title = $divi_powerful_archive_category_page_header_category_text;			} elseif  ( is_author() ) {				$divi_powerful_archive_category_page_header_pre_title = $divi_powerful_archive_category_page_header_author_text;						} elseif  ( is_archive() && 'post' == get_post_type() ) {				$divi_powerful_archive_category_page_header_pre_title = $divi_powerful_archive_category_page_header_archive_text;						} elseif  ( is_tax() ) {				$dp_term = get_term_by( 'slug', get_query_var( 'term' ), get_query_var( 'taxonomy' ) );				$dp_tax = get_taxonomy( $dp_term->taxonomy );				$divi_powerful_archive_category_page_header_pre_title = $dp_tax->labels->singular_name . $divi_powerful_archive_category_page_header_cpt_separator;			} elseif  ( is_search() ) {				$divi_powerful_archive_category_page_header_pre_title = $divi_powerful_archive_category_page_header_search_text;			}		}	}		return $divi_powerful_archive_category_page_header_pre_title;}// -------------- Archive Pre-Title Shortcode End ----------------// -------------- Archive Title Shortcode Start ----------------function divi_powerful_archive_category_page_header_title() {	$divi_powerful_archive_category_page_header_blog_posts_page_text = get_option( 'divi_powerful_archive_category_page_header_blog_posts_page_text', 'Latest Blogs' );	$divi_powerful_archive_category_page_header_cpt_prefix = get_option( 'divi_powerful_archive_category_page_header_cpt_prefix', 'Latest ' );	$divi_powerful_archive_category_page_header_title = '';		// $classes = get_body_class();	// if (in_array('et-pb-preview',$classes)) {		if ( is_et_pb_preview() ) {		$divi_powerful_archive_category_page_header_title = '[Title]';	} else {			if  ( is_tag() || is_category() || is_author() || is_archive() || is_home() || is_search() ) {			if  ( is_home() ) {				$divi_powerful_archive_category_page_header_title = $divi_powerful_archive_category_page_header_blog_posts_page_text;			} elseif  ( is_tag() ) {				$divi_powerful_archive_category_page_header_title = single_term_title( '', false);						} elseif  ( is_category() ) {				$divi_powerful_archive_category_page_header_title = single_cat_title( '', false);					} elseif  ( is_author() ) {				$divi_powerful_archive_category_page_header_title = get_the_author_meta( 'display_name' );					} elseif  ( is_post_type_archive() ) {				$divi_powerful_archive_category_page_header_title = post_type_archive_title( $divi_powerful_archive_category_page_header_cpt_prefix, false);			} elseif  ( is_tax() ) {				$divi_powerful_archive_category_page_header_title = single_term_title( '', false);			} elseif  ( is_archive() ) {				$divi_powerful_archive_category_page_header_title = get_the_archive_title();					} elseif  ( is_search() ) {				$divi_powerful_archive_category_page_header_title = get_search_query();			}		}	}		return $divi_powerful_archive_category_page_header_title;}// -------------- Archive Title Shortcode End ----------------// -------------- Archive Description Shortcode Start ----------------function divi_powerful_archive_category_page_header_description() {	$divi_powerful_archive_category_page_header_description_text = '';		if ( is_et_pb_preview() ) {		$divi_powerful_archive_category_page_header_description_text = '[Description]';	} else {			if  ( is_tax() || is_category() || is_tag() ) {			$divi_powerful_archive_category_page_header_description_text = term_description();		}	}	return $divi_powerful_archive_category_page_header_description_text;}// -------------- Archive Description Shortcode End ----------------// -------------- Archive Breadcrumbs Shortcode Start ----------------function divi_powerful_archive_breadcrumbs() {		if(function_exists('bcn_display')) {      $breadcrumb_output = '<div class="breadcrumbs" xmlns:v="http://rdf.data-vocabulary.org/#">';      $breadcrumb_output .= bcn_display( true );      $breadcrumb_output .= '</div>';      return $breadcrumb_output;   } else {		$divi_powerful_archive_category_page_header_breadcrumbs_home_text = get_option( 'divi_powerful_archive_category_page_header_breadcrumbs_home_text', 'Home' );		$divi_powerful_archive_category_page_header_breadcrumbs_blog_text = get_option( 'divi_powerful_archive_category_page_header_breadcrumbs_blog_text', 'Blog' );		$divi_powerful_breadcrumbs_home_icon = get_option( 'divi_powerful_breadcrumbs_home_icon' );		$dp_divider = " <span class='free-breadcrumbs-sep'></span> ";		$dp_blog_posts_page = get_option( 'page_for_posts' );				$divi_powerful_archive_breadcrumbs = '<div class="free-breadcrumbs"><div class="free-breadcrumbs-container">';				if (!is_front_page()) {			$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . '<a class="free-breadcrumbs-home" href="' . get_option('home') . '">';			if ( !$divi_powerful_breadcrumbs_home_icon ) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . $divi_powerful_archive_category_page_header_breadcrumbs_home_text;			}			$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . '</a> '. $dp_divider;			// Blog Page Link			if ( ( is_category() || is_single() || is_archive() || is_tag() || is_author() ) && !empty( $dp_blog_posts_page ) && 'post' == get_post_type() ) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . '<a class="free-breadcrumbs-blog-posts-page" href="' . get_permalink( $dp_blog_posts_page ) . '">' . $divi_powerful_archive_category_page_header_breadcrumbs_blog_text . '</a>' . $dp_divider;			}						// CPT link			if ( is_tax() ) {				$dp_term = get_term_by( 'slug', get_query_var( 'term' ), get_query_var( 'taxonomy' ) );				$dp_tax = get_taxonomy( $dp_term->taxonomy );				$dp_cpt = $dp_tax->object_type[0];				$dp_obj = get_post_type_object( $dp_cpt );				$dp_name = $dp_obj->labels->name;				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . '<a class="free-breadcrumbs-cpt-archive-page" href="' . get_post_type_archive_link( $dp_cpt ) . '">' . $dp_name . '</a>' . $dp_divider;			}						if ( is_home() ) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . $divi_powerful_archive_category_page_header_breadcrumbs_blog_text;			}						// Category Page			if ( is_category() ) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . single_cat_title( '', false);			// Single Post			} elseif (is_single()) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . get_the_category_list(', ') . $dp_divider . get_the_title();			// Single Page			} elseif (is_page()) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . get_the_title();							// Tag Page			} elseif (is_tag()) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . single_term_title( '', false);			// Author Page			} elseif (is_author()) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . get_the_author_meta( 'display_name' );							// CPT Taxonomy Page			} elseif (is_tax()) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . single_term_title( '', false);							// CPT Main Archive Page			} elseif (is_post_type_archive()) {				$dp_obj = get_queried_object();				$dp_name = $dp_obj->labels->name;				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . $dp_name;							// Archive Page			} elseif (is_archive()) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . get_the_archive_title();							// 404 Page			} elseif (is_404()) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . '404';						// Search Page			} elseif (is_search()) {				$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . 'Search Results for: <span>' . get_search_query() . '</span>';			}					// Everything Else			// else {				// $divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . get_bloginfo('name');			// }		}		$divi_powerful_archive_breadcrumbs = $divi_powerful_archive_breadcrumbs . '</div></div>';					return $divi_powerful_archive_breadcrumbs;   }}// -------------- Archive Breadcrumbs Shortcode End ----------------// -------------- Archive Blog CSS Start ----------------function divi_powerful_archive_category_page_blog_css() {	$divi_powerful_archive_category_page_blog_styling_settings = get_option( 'divi_powerful_archive_category_page_blog_styling_settings', 'grid-2-col-sidebar' );		$dp_cat_layout = 'none';	if (isset($_GET['dp_cat_layout'])) {	  $dp_cat_layout = $_GET['dp_cat_layout'];	}		if ( file_exists(get_stylesheet_directory() . '/dp-assets/archive-blog/'. $dp_cat_layout . '.php') ) {		wp_enqueue_style('divi-powerful-archive-category-page-blog-css-query', get_stylesheet_directory_uri() . '/dp-assets/archive-blog/' . $dp_cat_layout . '.css', array(), DIVI_POWERFUL_VERSION);	} else {		if ( file_exists(get_stylesheet_directory() . '/dp-assets/archive-blog/'. $divi_powerful_archive_category_page_blog_styling_settings . '.php') && ($divi_powerful_archive_category_page_blog_styling_settings != 'none') ) {			wp_enqueue_style('divi-powerful-archive-category-page-blog-css', get_stylesheet_directory_uri() . '/dp-assets/archive-blog/' . $divi_powerful_archive_category_page_blog_styling_settings . '.css', array(), DIVI_POWERFUL_VERSION);		}	}}// -------------- Archive Blog CSS End ----------------// -------------- Archive Blog Pagination Start ----------------function divi_powerful_archive_blog_pagination() {	$divi_powerful_archive_category_page_blog_pagination_page_text = get_option( 'divi_powerful_archive_category_page_blog_pagination_page_text', 'Page ' );	$divi_powerful_archive_category_page_blog_pagination_of_text = get_option( 'divi_powerful_archive_category_page_blog_pagination_of_text', ' of ' );     if( is_singular() )        return;     global $wp_query;     /** Stop execution if there's only 1 page */    if( $wp_query->max_num_pages <= 1 )        return;     $paged = get_query_var( 'paged' ) ? absint( get_query_var( 'paged' ) ) : 1;    $max   = intval( $wp_query->max_num_pages );     /** Add current page to the array */    if ( $paged >= 1 )        $links[] = $paged;     /** Add the pages around the current page to the array */    if ( $paged >= 3 ) {        $links[] = $paged - 1;        $links[] = $paged - 2;    }     if ( ( $paged + 2 ) <= $max ) {        $links[] = $paged + 2;        $links[] = $paged + 1;    }     echo '<div class="free-archive-blog-pagination"><span class="free-archive-blog-pages">' . $divi_powerful_archive_category_page_blog_pagination_page_text . $paged . $divi_powerful_archive_category_page_blog_pagination_of_text . $max . '</span><ul>' . "\n";     /** Previous Post Link */    if ( get_previous_posts_link() )        printf( '<li class="free-previous-link-wrapper">%s</li>' . "\n", get_previous_posts_link('«') );     /** Link to first page, plus ellipses if necessary */    if ( ! in_array( 1, $links ) ) {        $class = 1 == $paged ? ' class="active"' : '';         printf( '<li%s><a%s href="%s">%s</a></li>' . "\n", $class, $class, esc_url( get_pagenum_link( 1 ) ), '1' );         if ( ! in_array( 2, $links ) )            echo '<li class="free-excess-wrapper"><span class="free-excess">…</span></li>';    }     /** Link to current page, plus 2 pages in either direction if necessary */    sort( $links );    foreach ( (array) $links as $link ) {        $class = $paged == $link ? ' class="active"' : '';        printf( '<li%s><a%s href="%s">%s</a></li>' . "\n", $class, $class, esc_url( get_pagenum_link( $link ) ), $link );    }     /** Link to last page, plus ellipses if necessary */    if ( ! in_array( $max, $links ) ) {        if ( ! in_array( $max - 1, $links ) )            echo '<li class="free-excess-wrapper"><span class="free-excess">…</span></li>' . "\n";         $class = $paged == $max ? ' class="active"' : '';        printf( '<li%s><a%s href="%s">%s</a></li>' . "\n", $class, $class, esc_url( get_pagenum_link( $max ) ), $max );    }     /** Next Post Link */    if ( get_next_posts_link() )        printf( '<li class="free-next-link-wrapper">%s</li>' . "\n", get_next_posts_link('»') );     echo '</ul></div>' . "\n"; }// -------------- Archive Blog Pagination End ----------------// -------------- Post Meta Override Start ----------------if ( ! function_exists( 'et_pb_postinfo_meta' ) ) {	function et_pb_postinfo_meta( $postinfo, $date_format, $comment_zero, $comment_one, $comment_more ){		$postinfo_meta = '';		if ( in_array( 'author', $postinfo ) )			$postinfo_meta .= ' ' . ' <span class="free-post-meta-author author vcard">' . esc_html__( 'by', 'et_builder' ) . ' ' . et_pb_get_the_author_posts_link() . '</span>';		if ( in_array( 'date', $postinfo ) ) {			if ( in_array( 'author', $postinfo ) ) $postinfo_meta .= ' | ';			$postinfo_meta .= '<span class="free-post-meta-date published">' . esc_html( get_the_time( wp_unslash( $date_format ) ) ) . '</span>';		}		if ( in_array( 'categories', $postinfo ) ) {			$categories_list = get_the_category_list(', ');			// do not output anything if no categories retrieved			if ( '' !== $categories_list ) {				if ( in_array( 'author', $postinfo ) || in_array( 'date', $postinfo ) )	$postinfo_meta .= ' | ';				$postinfo_meta .= '<span class="free-post-meta-category">' . $categories_list . '</span>';			}		}		if ( in_array( 'comments', $postinfo ) ){			if ( in_array( 'author', $postinfo ) || in_array( 'date', $postinfo ) || in_array( 'categories', $postinfo ) ) $postinfo_meta .= ' | ';			$postinfo_meta .= et_pb_get_comments_popup_link( $comment_zero, $comment_one, $comment_more );		}		return $postinfo_meta;	}}// -------------- Post Meta Override End ----------------// -------------- Meta Box Custom Header & Footer Start ----------------function divi_powerful_meta_box_markup() {	require_once get_template_directory() . '/includes/builder/feature/Library.php';	$dp_custom_header_overrides = get_post_meta(get_the_ID(), "dp-meta-custom-header", true);	$dp_custom_footer_overrides = get_post_meta(get_the_ID(), "dp-meta-custom-footer", true);	?>		<p>You can choose to override custom header / footer for this page.</p>	<p class="post-attrib5utes-label-wrapper">		<label class="post-attributes-label" for="dp-meta-custom-header">Custom Header</label>	</p>			<select name="dp-meta-custom-header">		<?php		$divi_powerful_header_styling_choices_array = array();		$divi_powerful_header_styling_choices_array['default'] = 'Default (Global Settings)';		$divi_powerful_header_styling_choices_array['none'] = 'Use default Divi Header';		$args = array( 'post_type' => 'et_pb_layout', 'numberposts' => -1,					'tax_query' => array(						array(							'taxonomy' => 'layout_category',							'field' => 'slug',							'terms' => 'header-navigation-divi-powerful-child-theme'						)					)				 );		$divi_powerful_header_styles = get_posts( $args );		if ($divi_powerful_header_styles) {			foreach( $divi_powerful_header_styles as $divi_powerful_header_style ) :				$divi_powerful_header_styling_choices_array[$divi_powerful_header_style -> ID] = $divi_powerful_header_style -> post_title;			endforeach;		}			        foreach($divi_powerful_header_styling_choices_array as $value => $text) {            if($value == $dp_custom_header_overrides) { ?>                <option value="<?php echo $value; ?>" selected><?php echo $text; ?></option>            <?php }            else { ?>                <option value="<?php echo $value; ?>" ><?php echo $text; ?></option>			<?php }        }        ?>	</select>		<p class="post-attrib5utes-label-wrapper">		<label class="post-attributes-label" for="dp-meta-custom-footer">Custom Footer</label>	</p>			<select name="dp-meta-custom-footer">		<?php		$divi_powerful_footer_styling_choices_array = array();		$divi_powerful_footer_styling_choices_array['default'] = 'Default (Global Settings)';		$divi_powerful_footer_styling_choices_array['none'] = 'Use default Divi Footer';		$args = array( 'post_type' => 'et_pb_layout', 'numberposts' => -1,					'tax_query' => array(						array(							'taxonomy' => 'layout_category',							'field' => 'slug',							'terms' => 'footer-divi-powerful-child-theme'						)					)				 );		$divi_powerful_footer_styles = get_posts( $args );		if ($divi_powerful_footer_styles) {			foreach( $divi_powerful_footer_styles as $divi_powerful_footer_style ) :				$divi_powerful_footer_styling_choices_array[$divi_powerful_footer_style -> ID] = $divi_powerful_footer_style -> post_title;			endforeach;		}			        foreach($divi_powerful_footer_styling_choices_array as $value => $text) {            if($value == $dp_custom_footer_overrides) { ?>                <option value="<?php echo $value; ?>" selected><?php echo $text; ?></option>            <?php }            else { ?>                <option value="<?php echo $value; ?>" ><?php echo $text; ?></option>			<?php }        }        ?>	</select>    <?php}function divi_powerful_meta_box() {		$exclude_cpts = array('et_pb_layout');		if ($cpts = get_post_types()) {		foreach ($cpts as $cpt) {			if (in_array($cpt, $exclude_cpts)) {				continue;			}			add_meta_box('divi-powerful-meta-box', 'Divi Powerful', 'divi_powerful_meta_box_markup', $cpt, 'side', 'low', null);		}	}}// -------------- Meta Box Custom Header & Footer End ----------------// -------------- Meta Box Save Start ----------------function divi_powerful_meta_box_save($post_id, $post, $update) {	if (!current_user_can("edit_post", $post_id)) return;	if (defined("DOING_AUTOSAVE") && DOING_AUTOSAVE) return;	$post_type = get_post_type($post_id);	    if(isset($_POST["dp-meta-custom-header"])) {        update_post_meta($post_id, "dp-meta-custom-header", $_POST["dp-meta-custom-header"]);    }       if(isset($_POST["dp-meta-custom-footer"])) {        update_post_meta($post_id, "dp-meta-custom-footer", $_POST["dp-meta-custom-footer"]);    }}// -------------- Meta Box Save End ----------------// -------------- Load Custom Modules Start ----------------function divi_powerful_custom_modules(){    if(class_exists("ET_Builder_Module")){		include(get_stylesheet_directory() . '/dp-assets/modules/dp-regular-menu/dp-regular-menu.php');    }}// -------------- Load Custom Modules End ----------------// -------------- WP Admin CSS Start ----------------function divi_powerful_wp_admin_css(){	echo '<style>			.et-fb-settings-option-inner-upload--previewable .et-fb-settings-option-input.et-fb-settings-option-upload {				display: block!important;				width: 100%!important;				margin-top: 42px;			}			.et-fb-settings-option-inner-upload--previewable:after {				margin: 0 0 4px;				color: #32373c;				font-size: .875rem;				font-weight: 600;				content: \'Background Image URL\';				position: absolute;				bottom: 33px;			}			html .et_pb_library_page_top-layout .et-fb-modal__support-notice {				display: none!i